package com.voidapp.magizhiniorganics.magizhiniorganics.Firestoreimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.CustomerProfileimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.Messagesimport com.voidapp.magizhiniorganics.magizhiniorganics.ui.customerSupport.ChatViewModelimport com.voidapp.magizhiniorganics.magizhiniorganics.ui.customerSupport.chatConversation.ConversationViewModelclass FirebaseRepository (    private val firebase: Firebase) {    suspend fun createNewCustomerProfile() = firebase.createNewCustomerProfile()    suspend fun uploadUserProfile(profile: CustomerProfile):Boolean = firebase.uploadUserProfile(profile)    fun updateTypingStatus(id: String, status: Boolean) = firebase.updateTypingStatus(id, status)    fun sendMessage(message: Messages) = firebase.sendMessage(message)    suspend fun getConversation(conversationViewModel: ConversationViewModel, fromId: String, toId: String) = firebase.getConversation(conversationViewModel, fromId, toId)    suspend fun listenForRecentMessages(chatViewModel: ChatViewModel, id: String) = firebase.listenForRecentMessages(chatViewModel, id)    fun updateProfileStatus(id: String ,status: Boolean, timestamp: Long) = firebase.updateProfileStatus(id ,status, timestamp)    fun getAllSupportProfiles(chatViewModel: ChatViewModel) = firebase.getAllSupportProfiles(chatViewModel)    suspend fun supportStatusListener(supportID: String, viewModel: ConversationViewModel) = firebase.supportStatusListener(supportID, viewModel)}