package com.voidapp.magizhiniorganics.magizhiniorganics.adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.core.content.ContextCompatimport androidx.recyclerview.widget.RecyclerViewimport com.voidapp.magizhiniorganics.magizhiniorganics.Rimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.SupportProfileimport com.voidapp.magizhiniorganics.magizhiniorganics.databinding.RvChatItemBindingclass ContactsAdapter(    private val context: Context,    var supportProfiles: MutableList<SupportProfile>,    private val onItemClickListener: ContactItemClickListener): RecyclerView.Adapter<ContactsAdapter.ContactsViewHolder>() {    inner class ContactsViewHolder(val binding: RvChatItemBinding): RecyclerView.ViewHolder(binding.root)    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ContactsViewHolder {        val view = RvChatItemBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return ContactsViewHolder(view)    }    override fun onBindViewHolder(holder: ContactsViewHolder, position: Int) {            val supportProfile = supportProfiles[position]            with(holder.binding) {                tvProfileName.text = supportProfile.name                ivProfileImage.setImageDrawable(ContextCompat.getDrawable(context, R.drawable.ic_profile_placeholder))                tvRecentMessage.text = supportProfile.number                tvStatus.visibility = View.GONE                llHeader.setOnClickListener {                    onItemClickListener.getHelp(supportProfile.number)                }            }    }    override fun getItemCount(): Int {        return supportProfiles.size    }    interface ContactItemClickListener {        fun getHelp(number: String)    }}