package com.voidapp.magizhiniorganics.magizhiniorganics.adapterimport android.content.Contextimport android.content.res.ColorStateListimport android.graphics.Paintimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.*import androidx.core.content.ContextCompatimport androidx.lifecycle.ViewModelimport androidx.recyclerview.widget.RecyclerViewimport com.google.android.material.imageview.ShapeableImageViewimport com.voidapp.magizhiniorganics.magizhiniorganics.Rimport com.voidapp.magizhiniorganics.magizhiniorganics.data.entities.CartEntityimport com.voidapp.magizhiniorganics.magizhiniorganics.ui.checkout.CheckoutViewModelimport com.voidapp.magizhiniorganics.magizhiniorganics.ui.purchaseHistory.PurchaseHistoryViewModelimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.Constantsimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.GlideLoaderimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.SharedPrefclass OrderItemsAdapter(    private val context: Context,    var cartItems: List<CartEntity>,    private val viewModel: ViewModel,    var favorites: List<String> = listOf(),    private val content: String = "") : RecyclerView.Adapter<OrderItemsAdapter.OrderItemsViewHolder>() {    //TODO RV PADDING ON BOTTOM    //TODO BEFORE ADD PRODUCT A CHECK FOR LIMITED ITEM    inner class OrderItemsViewHolder(private val itemView: View) : RecyclerView.ViewHolder(itemView) {        //details views        val productThumbNail: ShapeableImageView = itemView.findViewById(R.id.ivProductThumbnail)        val orderCount: TextView = itemView.findViewById(R.id.tvOrderItemCount)        val productName: TextView = itemView.findViewById<TextView>(R.id.tvProductName)        val price: TextView = itemView.findViewById<TextView>(R.id.tvDiscount)        val discountedAmount: TextView = itemView.findViewById<TextView>(R.id.tvPrice)        val variants: Spinner = itemView.findViewById<Spinner>(R.id.spProductVariant)        val addBtn: TextView = itemView.findViewById(R.id.tvAddItem)        val favorites: ShapeableImageView = itemView.findViewById(R.id.ivFavourite)        val review: ShapeableImageView = itemView.findViewById(R.id.ivReview)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): OrderItemsViewHolder {        val view =            LayoutInflater.from(parent.context).inflate(R.layout.rv_list_item, parent, false)        return OrderItemsViewHolder(view)    }    override fun onBindViewHolder(holder: OrderItemsViewHolder, position: Int) {        //setting up the basic variables        val cartItem = cartItems[position]        val id = cartItem.id        //getting the current user id so that favorites can be added to firestore data        val userId: String = SharedPref(context).getData(Constants.USER_ID, Constants.STRING, "").toString()        //loading the thumbnail        GlideLoader().loadUserPicture(context, cartItem.thumbnailUrl, holder.productThumbNail)        holder.productThumbNail.clipToOutline = true        holder.addBtn.visibility = View.INVISIBLE        holder.productName.text = cartItem.productName        holder.orderCount.text = "Purchased: ${cartItem.quantity}"        val variantArray = arrayListOf<String>()        var price = 0F        variantArray.add(cartItem.variant)        price = cartItem.price        val adapter =            ArrayAdapter(holder.variants.context, R.layout.support_simple_spinner_dropdown_item, variantArray)        holder.variants.adapter = adapter        holder.discountedAmount.text = "Rs. $price"        holder.price.text = "Rs. ${cartItem.originalPrice}"        holder.price.paintFlags = Paint.STRIKE_THRU_TEXT_FLAG        //setting the favorties icon for the products        if (favorites.contains(cartItem.productId)) {            holder.favorites.setImageResource(R.drawable.ic_favorite_filled)        } else {            holder.favorites.setImageResource(R.drawable.ic_favorite_outline)        }        if (content == "checkout") {            with(holder) {                review.visibility = View.INVISIBLE                favorites.visibility = View.INVISIBLE                addBtn.visibility = View.VISIBLE                addBtn.text = "Remove"                addBtn.backgroundTintList = ColorStateList.valueOf(ContextCompat.getColor(context, R.color.matteRed))                orderCount.visibility = View.GONE            }        }        //favorites click listener        holder.favorites.setOnClickListener {            var removedItem: String = ""            var addedItem: String = ""            //if the favorites array list that we got contains the clicked product id then it will be removed and notificatio will            //be sent to the view model with the current userID, position of the product so that itemchange can be notified only to that            //particular product once the product is added or remove from the favorites arraylist in the profile and then            // new array is added back to the adapter.            if (favorites.contains(cartItem.productId)) {                removedItem = cartItem.productId                holder.favorites.setImageResource(R.drawable.ic_favorite_outline)            } else {                addedItem = cartItem.productId                holder.favorites.setImageResource(R.drawable.ic_favorite_filled)            }            when(viewModel) {                is PurchaseHistoryViewModel -> viewModel.updateFavorites(userId, cartItem.productId, addedItem, removedItem)            }        }//        holder.addBtn.setOnClickListener {//            if (holder.addBtn.text == "Add") {//                when(viewModel) {//                    is PurchaseHistoryViewModel -> viewModel.upsertCartItem(//                        cartItem.productId, cartItem.productName, cartItem.thumbnailUrl ,cartItem.variant, 1,cartItem.price, cartItem.originalPrice//                        setRemoveButton()//                    )//                }//            }//        }        holder.productThumbNail.setOnClickListener {            when (viewModel) {                is PurchaseHistoryViewModel -> viewModel.moveToProductDetails(cartItem.productId, cartItem.productName)            }        }        holder.review.setOnClickListener {            when (viewModel) {                is PurchaseHistoryViewModel -> viewModel.moveToProductDetails(cartItem.productId, cartItem.productName)            }        }        holder.addBtn.setOnClickListener {            when (viewModel) {                is CheckoutViewModel -> {                    viewModel.deleteCartItem(id ,cartItem.productId, cartItem.variant)                    holder.addBtn.text = "Removed"                    holder.addBtn.isEnabled = false                }            }        }    }    override fun getItemCount(): Int {        return cartItems.size    }}