package com.voidapp.magizhiniorganics.magizhiniorganics.data.entitiesimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKeyimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.TransactionHistory@Entitydata class WalletEntity(    @PrimaryKey(autoGenerate = false)    var id: String = "",    @ColumnInfo    var amount: Float = 0F,    @ColumnInfo    var reminder: Boolean = true,    @ColumnInfo    var nextRecharge: Long = 0L,    @ColumnInfo    var transactionHistory: List<TransactionHistory> = listOf())