package com.voidapp.magizhiniorganics.magizhiniorganics.data.modelsimport android.provider.SyncStateContractimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.Constantsimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.Constants.WALLETdata class TransactionHistory(    var id: String = "",    var timestamp: Long = 0L,    var month: String = "",    var year: Long = 2021,    var amount: Float = 0F,    var fromID: String = "",    var fromUPI: String = "",    var status: String = Constants.SUCCESS,    var purpose: String = Constants.PURCHASE,    var transactionFor: String = "" //this is the orderid or wallet payment)data class GlobalTransaction(    var id: String = "",    var userID: String = "",    var userName: String = "",    var userMobileNumber: String = "",    var transactionID: String = "",    var transactionType: String = WALLET,   //Wallet or Online to indicate the transaction type from account    var transactionAmount: Float = 0f,    var transactionDirection: String = "Add",   //Add or Remove to indicate the transaction direction from account    var timestamp: Long = 0L,    var transactionReason: String = "",    var verified: Boolean = false)