package com.voidapp.magizhiniorganics.magizhiniorganics.ui.customerSupportimport android.util.Logimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.google.firebase.firestore.auth.Userimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirebaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirestoreRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.dao.DatabaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.entities.UserProfileEntityimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.CustomerProfileimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.Messagesimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.SupportProfileimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.UserProfileimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextimport java.sql.Arrayclass ChatViewModel(    private val dbRepository: DatabaseRepository,    private val fsRepository: FirestoreRepository,    private val fbRepository: FirebaseRepository): ViewModel() {    var selectedIdForSupport = SupportProfile()    var profile: UserProfileEntity = UserProfileEntity()    var profileName: String = ""    private var _currentUserProfile: MutableLiveData<UserProfileEntity> = MutableLiveData()    val currentUserProfile: LiveData<UserProfileEntity> = _currentUserProfile    private var _moveToConversation: MutableLiveData<Boolean> = MutableLiveData()    val moveToConversation : LiveData<Boolean> = _moveToConversation    private var _recentMessage: MutableLiveData<ArrayList<Messages>> = MutableLiveData()    val recentMessage: LiveData<ArrayList<Messages>> = _recentMessage    private var _supportProfiles: MutableLiveData<ArrayList<SupportProfile>> = MutableLiveData()    val supportProfiles: LiveData<ArrayList<SupportProfile>> = _supportProfiles    fun getProfileData() = viewModelScope.launch(Dispatchers.IO) {        profile = dbRepository.getProfileData()!!        withContext(Dispatchers.Main) {            _currentUserProfile.value = profile            profileName = profile.name            listenForRecentMessages(profile.id)        }    }    fun listenForRecentMessages(id: String) = fbRepository.listenForRecentMessages(this, id)    fun moveToConversations(chatItem: SupportProfile) {        selectedIdForSupport = chatItem        _moveToConversation.value = true    }    fun updateRecentMessages(messages: HashMap<String, Messages>) {        val message = arrayListOf<Messages>()        message.addAll(messages.values)        message.sortByDescending {            it.timeStamp        }        _recentMessage.value = message    }    fun updateProfileStatus(id: String ,status: Boolean, timestamp: Long = 0L) = fbRepository.updateProfileStatus(id, status, timestamp)    fun getAllSupportProfiles() = fbRepository.getAllSupportProfiles(this)    fun updateAllSupportProfiles(supportProfiles: ArrayList<SupportProfile>) {        _supportProfiles.value = supportProfiles    }}