package com.voidapp.magizhiniorganics.magizhiniorganics.ui.customerSupportimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirebaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirestoreRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.dao.DatabaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.entities.UserProfileEntityimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextclass ChatViewModel(    private val dbRepository: DatabaseRepository,    private val fsRepository: FirestoreRepository,    private val fbRepository: FirebaseRepository): ViewModel() {    var profile: UserProfileEntity? = null    private var _currentUserProfile: MutableLiveData<UserProfileEntity?> = MutableLiveData()    val currentUserProfile: LiveData<UserProfileEntity?> = _currentUserProfile    fun getProfileData() = viewModelScope.launch(Dispatchers.IO) {        profile = dbRepository.getProfileData()        withContext(Dispatchers.Main) {            profile?.let {                _currentUserProfile.value = it            } ?: let {                _currentUserProfile.value = null            }        }    }}