package com.voidapp.magizhiniorganics.magizhiniorganics.ui.customerSupportimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirebaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirestoreRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.dao.DatabaseRepositoryclass ChatViewModelFactory(    private val dbRepository: DatabaseRepository,    private val fsRepository: FirestoreRepository,    private val fbRepository: FirebaseRepository) : ViewModelProvider.NewInstanceFactory() {    override fun <T : ViewModel> create(modelClass: Class<T>): T {        return ChatViewModel(dbRepository, fsRepository, fbRepository) as T    }}