package com.voidapp.magizhiniorganics.magizhiniorganics.ui.walletimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirestoreRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.dao.DatabaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.entities.WalletEntityimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.TransactionHistoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.Walletimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.Constantsimport com.voidapp.magizhiniorganics.magizhiniorganics.utils.Timeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextclass WalletViewModel(    val dbRepository: DatabaseRepository,    val fbRepository: FirestoreRepository): ViewModel() {    private var _wallet: MutableLiveData<Wallet> = MutableLiveData()    val wallet: LiveData<Wallet> = _wallet    private var _transactions: MutableLiveData<List<TransactionHistory>> = MutableLiveData()    val transactions: LiveData<List<TransactionHistory>> = _transactions    private var _userID: MutableLiveData<String> = MutableLiveData()    val userID: LiveData<String> = _userID    private var _userName: MutableLiveData<String> = MutableLiveData()    val userName: LiveData<String> = _userName    private var _userPhoneNumber: MutableLiveData<String> = MutableLiveData()    val userPhoneNumber: LiveData<String> = _userPhoneNumber    fun getWallet(id: String) = viewModelScope.launch(Dispatchers.IO) {        val wallet = fbRepository.getWallet(id)        withContext(Dispatchers.Main) {            _wallet.value = wallet        }    }    fun getTransactions(id: String) = viewModelScope.launch (Dispatchers.IO) {        val transactions = fbRepository.getTransactions(id)        withContext(Dispatchers.Main) {            _transactions.value = transactions        }    }    fun getUserProfileData() = viewModelScope.launch(Dispatchers.IO) {        val profile = dbRepository.getProfileData()!!        withContext(Dispatchers.Main) {            _userName.value = profile.name            _userPhoneNumber.value = profile.phNumber            _userID.value = profile.id        }    }    suspend fun createTransaction(amount: Float, id: String, orderID: String): String {        if (fbRepository.makeTransactionFromWallet(amount, id, "Add")) {            val transaction = TransactionHistory (                id,                System.currentTimeMillis(),                Time().getMonth(),                Time().getYear().toLong(),                amount,                id,                orderID,                Constants.SUCCESS,                Constants.ADD_MONEY,                orderID            )            return fbRepository.updateTransaction(transaction)        } else {            return "failed"        }    }}