package com.voidapp.magizhiniorganics.magizhiniorganics.ui.walletimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.voidapp.magizhiniorganics.magizhiniorganics.Firestore.FirestoreRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.dao.DatabaseRepositoryimport com.voidapp.magizhiniorganics.magizhiniorganics.data.entities.WalletEntityimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.Walletimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextclass WalletViewModel(    val dbRepository: DatabaseRepository,    val fbRepository: FirestoreRepository): ViewModel() {    private var _wallet: MutableLiveData<WalletEntity> = MutableLiveData()    val wallet: LiveData<WalletEntity> = _wallet    fun getWallet() = viewModelScope.launch(Dispatchers.IO) {        val wallet = dbRepository.getWallet()        withContext(Dispatchers.Main) {            _wallet.value = wallet        }    }}