package com.voidapp.magizhiniorganics.magizhiniorganics.utils.dialogsimport android.content.Contextimport android.view.LayoutInflaterimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.google.android.material.bottomsheet.BottomSheetDialogimport com.voidapp.magizhiniorganics.magizhiniorganics.Rimport com.voidapp.magizhiniorganics.magizhiniorganics.adapter.OrderItemsAdapterclass ItemsBottomSheet(    private val context: Context,    private val orderItemsAdapter: OrderItemsAdapter){    private val bottomSheetDialog: BottomSheetDialog = BottomSheetDialog(context, R.style.BottomSheetDialog)    init {        val view = LayoutInflater.from(context).inflate(R.layout.dialog_bottom_item_list, null)        bottomSheetDialog.setContentView(view)        val recyclerViewItems: RecyclerView = view.findViewById(R.id.rvItemsList)        with(view) {            recyclerViewItems.layoutManager = LinearLayoutManager(context, LinearLayoutManager.VERTICAL, false)            recyclerViewItems.adapter = orderItemsAdapter        }    }    fun show() = bottomSheetDialog.show()    fun dismiss() = bottomSheetDialog.dismiss()}