package com.voidapp.magizhiniorganics.magizhiniorganics.utils.diffUtilsimport androidx.recyclerview.widget.DiffUtilimport com.voidapp.magizhiniorganics.magizhiniorganics.data.entities.CartEntityclass CartDiffUtil(private val oldList: List<CartEntity>,private val newList: List<CartEntity>): DiffUtil.Callback() {    override fun getOldListSize(): Int {        return oldList.size    }    override fun getNewListSize(): Int {        return newList.size    }    override fun areItemsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        oldList        return oldList[oldItemPosition].id == newList[newItemPosition].id    }    override fun areContentsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        return when {            oldList[oldItemPosition].id !== newList[newItemPosition].id -> {                false            }            oldList[oldItemPosition].variant !== newList[newItemPosition].variant -> {                false            }            oldList[oldItemPosition].productId !== newList[newItemPosition].productId -> {                false            }            oldList[oldItemPosition].thumbnailUrl !== newList[newItemPosition].thumbnailUrl -> {                false            }            oldList[oldItemPosition].productName !== newList[newItemPosition].productName -> {                false            }            oldList[oldItemPosition].quantity !== newList[newItemPosition].quantity -> {                false            }            oldList[oldItemPosition].maxOrderQuantity !== newList[newItemPosition].maxOrderQuantity -> {                false            }            oldList[oldItemPosition].price !== newList[newItemPosition].price -> {                false            }            oldList[oldItemPosition].originalPrice !== newList[newItemPosition].originalPrice -> {                false            }            oldList[oldItemPosition].couponName !== newList[newItemPosition].couponName -> {                false            }            else -> true        }    }}