package com.voidapp.magizhiniorganics.magizhiniorganics.utils.diffUtilsimport androidx.recyclerview.widget.DiffUtilimport com.voidapp.magizhiniorganics.magizhiniorganics.data.models.Messagesclass ChatDiffUtils(    private val oldList: ArrayList<Messages>,    private val newList: ArrayList<Messages>): DiffUtil.Callback() {    override fun getOldListSize(): Int {        return oldList.size    }    override fun getNewListSize(): Int {        return newList.size    }    override fun areItemsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        return oldList[oldItemPosition].id == newList[newItemPosition].id    }    override fun areContentsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        return when {            oldList[oldItemPosition].id == newList[newItemPosition].id -> {                true            }            oldList[oldItemPosition].fromId == newList[newItemPosition].fromId -> {                true            }            oldList[oldItemPosition].toId == newList[newItemPosition].toId -> {                true            }            oldList[oldItemPosition].type == newList[newItemPosition].type -> {                true            }            oldList[oldItemPosition].message == newList[newItemPosition].message -> {                true            }            oldList[oldItemPosition].timeStamp == newList[newItemPosition].timeStamp  -> {                true            }            oldList[oldItemPosition].seen == newList[newItemPosition].seen -> {                true            }            else -> true        }    }}